class Prices extends React.Component {
  state = {
    currency: "EUR"
  };

  render() {
    let list = "";
    console.log(this.state.currency);
    if (this.state.currency === "USD") {
      list = (
        <li className="list-group-item">
          Bitcoin rate for {this.props.bpi.USD.description}:{" "}
          <span className="badge badge-primary">{this.props.bpi.USD.code}</span>
          <strong>{this.props.bpi.USD.rate}</strong>
        </li>
      );
    } else if (this.state.currency === "GPB") {
      list = (
        <li className="list-group-item">
          Bitcoin rate for {this.props.bpi.GPB.description}:{" "}
          <span className="badge badge-primary">{this.props.bpi.GPB.code}</span>
          <strong>{this.props.bpi.GPB.rate}</strong>
        </li>
      );
    } else if (this.state.currency === "EUR") {
      list = (
        <li className="list-group-item">
          Bitcoin rate for {this.props.bpi.EUR.description}:{" "}
          <span className="badge badge-primary">{this.props.bpi.EUR.code}</span>
          <strong>{this.props.bpi.EUR.rate}</strong>
        </li>
      );
    }
    return (
      <div>
        Prices
        <ul className="list-group">{list}</ul>
        <select
          onChange={e => {
            console.log(e.currentTarget);
          }}
          name="currency"
        >
          <option disabled>Currency</option>
          <option value="EUR">EUR</option>
          <option value="GPB">GPB</option>
          <option value="USD">USD</option>
        </select>
      </div>
    );
  }
}
export default Prices;
